SELECT rut, nombres, apellido_paterno,apellido_materno, fecha_nacimiento, direccion,comuna,clave, correo, telefono, certificado_medico, enfermedades, observaciones 
                FROM Piscina.usuario AS user
                INNER JOIN Piscina.usuario_piscina AS userpiscina on userpiscina.rut_usuario=user.rut;

SELECT admin.rut as rut, nombres, apellido_paterno,apellido_materno, fecha_nacimiento, direccion,comuna,clave, correo, telefono, certificado_medico, enfermedades, observaciones
                FROM Piscina.usuario AS user, Piscina.usuario_piscina, Piscina.administrador_sistema AS admin 
                WHERE user.rut=? AND user.clave=? AND admin.rut=user.rut;
